import React, { Component } from "react";
import Node from "./node/node";
import { dijkstra } from "../algorithms/dijkstra.js";

const gridSizeCol = 10;
const gridSizeRow = 10;

class Pathing extends Component {
    constructor(props) {
        super(props);
        this.state = {
            grid: [],
            startSet: false,
            startNode: null,
            finishSet: false,
            finishNode: null,
        };
    }

    componentDidMount() {
        const grid = [];
        for (let row = 0; row < gridSizeCol; row++) {
            const currentrow = [];
            for (let col = 0; col < gridSizeRow; col++) {
                currentrow.push(this.createNode(row, col));
            }
            grid.push(currentrow);
        }
        this.setState({ grid });
    }

    createNode(col, row) {
        return {
            col,
            row,
            distance: Infinity,
            prevNode: null,
            isStart: false,
            isFinish: false,
            isWall: false,
            isVisited: false
        };
    }

    handleMouseDown(row, col, nodeClass) {
        
        if (nodeClass === "start") {
            this.state.grid[col][row].isStart = true;
            //console.log(this.state.grid[col][row]);
            this.setState({
                startSet: true,
                startNode: this.state.grid[col][row],
                grid: this.state.grid
            });
        } else if (nodeClass === "finish") {
            this.state.grid[col][row].isFinish = true;
            this.setState({
                finishSet: true,
                finishNode: this.state.grid[col][row],
                grid: this.state.grid
            });
        }else if (nodeClass === "wall") {
            this.state.grid[col][row].isWall = true;
            this.setState({
                grid: this.state.grid
            });
        }

        //console.log(`handle row-${row}, col-${col}`);
        
    }

    handleDijkstra() {
        const vistedNodes = dijkstra(
            this.state.startNode,
            this.state.finishNode,
            this.state.grid
        );

        for(const node of vistedNodes){
            node.isVisited = true;
            this.setState()
        }
        
    }

    render() {
        const { grid, startSet, finishSet } = this.state;
        console.log("full grid", grid);

        return (
            <div>
                <button onClick={() => this.handleDijkstra()}>Start</button>
                <div className="grid">
                    <div className="nodes">
                        {grid.map((row, rowindex) => {
                            return (
                                <div key={rowindex}>
                                    {row.map((node, nodeindex) => {
                                        const { row, col, isStart, isFinish, isWall, isVisited } = node;
                                        // prettier-ignore
                                        return (
                                            <Node
                                                key={nodeindex}
                                                row={row}
                                                col={col}
                                                startSet={startSet}
                                                finishSet={finishSet}
                                                isStart={isStart}
                                                isFinish={isFinish}
                                                isWall={isWall}
                                                isVisited={isVisited}
                                                onMouseDown={(row,col,nodeClass) => this.handleMouseDown(row, col, nodeClass)}
                                            ></Node>
                                        );
                                    })}
                                </div>
                            );
                        })}
                    </div>
                </div>
            </div>
        );
    }
}

export default Pathing;
