export function recursiveDivision(grid,row, col, height, width, orientation){
    if(width < 3 || height < 3)return;

    let isHorizontal = orientation;




    let wallcol = 0;
    let wallrow = 0;


    let newHeight1 = height;
    let newHeight2 = height;
    let newWidth1 = width;
    let newWidth2= width;
    let col2 = col;
    let row2 = row;
    
    if(isHorizontal){
        wallrow = row + getRandNum(row, (height - 2));
        newHeight1 = wallrow - row;
        newHeight2 = row + height - wallrow;
        

        for(let i = row; i < row+width; i++){
            grid[i][wallrow].isWall = true;
        }
    }else{
        wallcol = col + getRandNum(col, (width - 2));
        newWidth1 = wallcol - row;
        newWidth2 = row + width - wallcol;
       

        for(let i = col; i < col + height; i++){
            grid[wallcol][i].isWall = true; 
        }
    }
    row2 = wallrow;
    col2 = wallcol;
    const passageCol = wallcol + (isHorizontal ? getRandNum(row, (width - 1)) : 0);
    const passageRow = wallrow + (isHorizontal ? 0 : getRandNum(col, (height - 1)));
    grid[passageCol][passageRow].isWall = false;

    console.log("wall (r,c): ", wallrow, wallcol);
    console.log("pass (r,c): ", passageRow, passageCol);
    console.log('node: ',grid[passageCol][passageRow]);
    console.log("---------------------");



    recursiveDivision(grid, row, col, newHeight1, newWidth1, getOrientation(newHeight1, newWidth1));
    //recursiveDivision(grid, row2, col2, newHeight2, newWidth2, getOrientation(newHeight2, newWidth2));
    
}

export function getOrientation(height, width){
    if(width < height){//is horizontal
        return true;
    }else if(height < width){ // is vertical
        return false;
    }else{
        const rand = Math.floor(Math.random() * 2);
        return rand === 0  ? true : false;
    }
}

function getRandNum(min, max) {
    return Math.floor(Math.random() * (max - min + 1) ) + min;
