import React, { Component } from "react";
import "./node.css";

class Node extends Component {
    state = {
        row: this.props.row,
        col: this.props.col,
        isStart: false,
        isFinish: false,
        isWall: false,
        nodeClass: "",
    };

    nodeClick = () => {
        this.setNodeClass();
        this.setState({});
        console.log(`row-${this.state.row} col-${this.state.col}`);
    };

    setNodeClass() {
        // if (this.state.isStart === false && this.state.isFinish === false) {
             this.state.isWall = true;
             console.log(`row-${this.state.row} col-${this.state.col}`);
        // }
    }

    render() {
        return (
            <div>
                <div
                    className={`node ${this.state.nodeClass}`}
                    id={`row-${this.props.row} col-${this.props.col}`}
                    onClick={this.nodeClick}
                ></div>
            </div>
        );
    }
}

export default Node;
