import React, { Component } from "react";
import "./node.css";

class Node extends Component {
    // state = {
    //     row: this.props.row,
    //     col: this.props.col,
    //     isStart: this.props.isStart,
    //     isFinish: this.props.isFinish,
    //     isWall: this.props.isWall,
    //     nodeClass: "",
    //     isVisited: this.props.isVisited
    // };



    nodeClick = () => {
        let nc;
        if(this.props.startSet === false){
            nc = "start";
            this.setState({isStart: true, nodeClass: nc});
        }else if(this.props.finishSet === false && !this.state.isStart){
            nc = "finish";
            this.setState({isFinish: true, nodeClass: nc});
        }else if(!this.state.isStart && !this.state.isFinish){
            nc = "wall";
            this.setState({isWall: true, nodeClass: nc});
        }
        this.props.onMouseDown(this.state.row, this.state.col, nc);
      
    };

    



    render() {
        const visitedClass = this.props.isVisitedAnimate ? 'visited' : '';
        const pathClass = this.props.pathAnimate ? 'path' : '';
        //console.log(this.props.isVisited);
        return (
            <div>
                <div
                    className={`node ${this.state.nodeClass} ${visitedClass} ${pathClass}`}
                    id={`row-${this.props.row} col-${this.props.col}`}
                    onMouseDown={this.nodeClick}

                ></div>
            </div>
        );
    }
}

export default Node;
