    let isHorizontal = orientation;

    let newHeight1;
    let newHeight2;
    let newWidth1;
    let newWidth2;
    let col2;
    let row2;
    let wallcol = col;
    let wallrow = row;
    let passageCol;
    let passageRow;
    let hole;
    
    //row = y, col = x
    if(isHorizontal){
        wallrow = row + getRandNum(row, (height - 2));
        hole = col 

        newHeight1 = wallrow - row;
        newWidth1 = width;

        newHeight2 = row + height - wallrow;
        newWidth2 = width;

        row2 = wallrow;
        col2 = col;
        
        for(let i = col; i < col+width; i++){
            grid[i][wallrow].isWall = true;
        }
        grid[passageCol][passageRow].isWall = false;

