import React, { Component } from "react";
import Node from "./node/node";
import { dijkstra } from "../algorithms/dijkstra.js";

const gridSizeCol = 20;
const gridSizeRow = 20;

class Pathing extends Component {
    constructor(props) {
        super(props);
        this.state = {
            grid: [],
            startSet: false,
            startNode: null,
            finishSet: false,
            finishNode: null,
        };
    }

    componentDidMount() {
        const grid = [];
        for (let row = 0; row < gridSizeCol; row++) {
            const currentrow = [];
            for (let col = 0; col < gridSizeRow; col++) {
                currentrow.push(this.createNode(row, col));
            }
            grid.push(currentrow);
        }
        this.setState({ grid });
    }

    createNode(col, row) {
        return {
            col,
            row,
            distance: Infinity,
            prevNode: null,
            isStart: false,
            isFinish: false,
            isWall: false,
            nodeClass: "",
        };
    }

    handleMouseDown(row, col, nodeClass) {
        console.log(nodeClass);
        if (nodeClass === "start") {
            this.setState({
                startSet: true,
                startNode: this.state.grid[col][row],
            });
        } else if (nodeClass === "finish") {
            this.setState({
                finishSet: true,
                finishNode: this.state.grid[col][row],
            });
        }

        console.log(`handle row-${row}, col-${col}`);
    }

    handleDijkstra() {
        const temp = dijkstra(
            this.state.startNode,
            this.state.finishNode,
            this.state.grid
        );
    }

    render() {
        const { grid, startSet, finishSet } = this.state;
        console.log("full grid", grid);

        return (
            <div>
                <button onClick={() => this.handleDijkstra()}>Start</button>
                <div className="grid">
                    <div className="nodes">
                        {grid.map((row, rowindex) => {
                            return (
                                <div key={rowindex}>
                                    {row.map((node, nodeindex) => {
                                        const { row, col } = node;
                                        // prettier-ignore
                                        return (
                                            
                                            <Node
                                            
                                                key={nodeindex}
                                                row={row}
                                                col={col}
                                                startSet={startSet}
                                                finishSet={finishSet}
                                                
                                                onMouseDown={(row,col,nodeClass
) =>this.handleMouseDown(row,col,nodeClass)}
                                            ></Node>
                                        );
                                    })}
                                </div>
                            );
                        })}
                    </div>
                </div>
            </div>
        );
    }
}

const handleStart = (data) => {
    console.log(data);
};

export default Pathing;
