import React, { Component } from "react";
import Node from "./node/node";

const gridSize = 70;

class Pathing extends Component {
    constructor(props) {
        super(props);
        this.state = { grid: [], startSet: false, finishSet: false };
    }

    componentDidMount() {
        const grid = [];
        for (let row = 0; row < gridSize; row++) {
            const currentrow = [];
            for (let col = 0; col < gridSize; col++) {
                currentrow.push(this.createNode(row, col));
            }
            grid.push(currentrow);
        }
        this.setState({ grid });
    }

    createNode(col, row) {
        return { col, row };
    }

    handleMouseDown(row, col, nodeClass) {
        console.log(nodeClass);
        if (nodeClass === "start") {
            this.setState({ startSet: true });
            console.log(this.state.startSet);
        } else if (nodeClass === "finish") {
            this.setState({ finishSet: true });
        }
        console.log(`handle row-${row}, col-${col}`);
    }

    render() {
        const { grid, startSet, finishSet } = this.state;
        console.log("full grid", grid);

        return (
            <div>
                <button>Start</button>
                <div className="grid">
                    <div className="nodes">
                        {grid.map((row, rowindex) => {
                            return (
                                <div key={rowindex}>
                                    {row.map((node, nodeindex) => {
                                        const { row, col } = node;

                                        return (
                                            <Node
                                                key={nodeindex}
                                                row={row}
                                                col={col}
                                                startSet={startSet}
                                                finishSet={finishSet}
                                                onMouseDown={(
                                                    row,
                                                    col,
                                                    nodeClass
                                                ) =>
                                                    this.handleMouseDown(
                                                        row,
                                                        col,
                                                        nodeClass
                                                    )
                                                }
                                            ></Node>
                                        );
                                    })}
                                </div>
                            );
                        })}
                    </div>
                </div>
            </div>
        );
    }
}

const handleStart = (data) => {
    console.log(data);
};

export default Pathing;
