export function recursiveDivision(grid,node, height, width, orientation){
    if(width < 2 || height < 2)return ;

    let isHorizontal = orientation;

    //get the column or row that a wall will be drawn in
    const wallrow = node.row + (isHorizontal ? 0 : Math.floor(Math.random() * width - 2));
    const wallcol = node.col + (isHorizontal ? Math.floor(Math.random() * height - 2) : 0);
    console.log("wallrow", wallrow);
    console.log("wallcol", wallcol);

    //random node in the wall will be selected as a passage
    const passageRow = wallrow + (isHorizontal ? Math.floor(Math.random() * width) : 0);
    const passageCol = wallcol + (isHorizontal ? 0 : Math.floor(Math.random() * height));
    console.log("passrow", passageRow);
    console.log("passcol", passageCol);

    //draw the wall
    if(isHorizontal){
        for(let i = 0; i < width; i++){
            grid[i][wallcol].isWall = true;
        }
    }else{
        for(let i = 0; i < height; i++){
            grid[wallrow][i].isWall = true;
        }
    }
    grid[passageRow][passageCol].isWall = false;

    
    
}

export function getOrientation(height, width){
    if(width < height){//is horizontal
        return true;
    }else if(height < width){ // is vertical
        return false;
    }else{
        const rand = Math.floor(Math.random() * 2);
        return rand === 0  ? true : false;
    }
}
