export function recursiveDivision(grid,row, col, height, width, orientation){
    if(width < 3 || height < 3)return;

    let isHorizontal = orientation;

    //get the column or row that a wall will be drawn in
    const wallcol = (isHorizontal ? 0 : Math.floor(Math.random() * (width - 2)));
    const wallrow = (isHorizontal ? Math.floor(Math.random() * (height - 2)) : 0);
    console.log("wallcol", wallcol);
    console.log("wallrow", wallrow);

    //random node in the wall will be selected as a passage
    const passageCol = wallcol + (isHorizontal ? Math.floor(Math.random() * (width - 1)) : 0);
    const passageRow = wallrow + (isHorizontal ? 0 : Math.floor(Math.random() * (height - 1)));
    console.log("passrow", passageRow);
    console.log("passcol", passageCol);
    console.log('node: ',grid[passageCol][passageRow]);
    console.log("---------------------");

    //draw the wall
    if(isHorizontal){
        if(row < width){
            for(let i = row; i < width; i++){
                grid[i][wallrow].isWall = true;
            }
        }else{
            for(let i = wi; i < width; i++){
                grid[i][wallrow].isWall = true;
            }  
        }
    }else{
        for(let i = col; i < height; i++){
            grid[wallcol][i].isWall = true; 
        }
    }
    
    grid[passageCol][passageRow].isWall = false;


    let newHeight1 = height;
    let newHeight2 = height;
    let newWidth1 = width;
    let newWidth2= width;

    if(isHorizontal){
        newHeight1 =  wallrow + 1;
        newHeight2 = height - wallrow -1;
    }else{
        newWidth1 = wallcol + 1;
        newWidth2 = width - wallcol - 1;
    }
    //recursiveDivision(grid, wallrow, wallcol, newHeight1, newWidth1, getOrientation(newHeight1, newWidth1));
    recursiveDivision(grid, wallrow, wallcol, newHeight2, newWidth2, getOrientation(newHeight2, newWidth2));
    
}

export function getOrientation(height, width){
    if(width < height){//is horizontal
        return true;
    }else if(height < width){ // is vertical
        return false;
    }else{
        const rand = Math.floor(Math.random() * 2);
        return rand === 0  ? true : false;
    }
}
