import React, { Component } from "react";
import "./node.css";

class Node extends Component {
    state = {
        row: this.props.row,
        col: this.props.col,
        isStart: this.props.row,
        isFinish: this.props.row,
        isWall: false,
        nodeClass: "",
    };



    nodeClick = () => {
        // this.props.handleStart('test');
        if(this.props.startSet === false){
            this.setState({isStart: true, nodeClass: "start"});
        }else if(this.props.finishSet === false && !this.state.isStart){
            this.setState({isFinish: true, nodeClass: "finish"});
        }else {
            this.setState({isWall: true, nodeClass: "wall"});
            
        }
        this.props.onMouseDown(this.state.row, this.state.col, this.state.nodeClass);
    };

    



    render() {
        return (
            <div>
                <div
                    className={`node ${this.state.nodeClass}`}
                    id={`row-${this.props.row} col-${this.props.col}`}
                    
                    onMouseDown={this.nodeClick}
                ></div>
            </div>
        );
    }
}

export default Node;
