export function dijkstra(start, end, grid) {
    let unvistedNodes = getNodes(grid);
    let vistedNodes = [];
    start.distance = 0;
    start.isVisited = true;
    while (unvistedNodes.length > 0) {
        sortNodes(unvistedNodes);
        const currentNode = unvistedNodes.shift();
        console.log("currentnode", currentNode);
        
        if(currentNode === end) return vistedNodes;
        getNeighbors(currentNode, vistedNodes ,grid);
        console.log('vn-',vistedNodes);
        
    }
    return vistedNodes;
}

//turns the 2d array into a 1d array for easier usage
function getNodes(grid) {
    const newGrid = [];
    for (const row of grid) {
        for (const node of row) {
            newGrid.push(node);
        }
    }
    return newGrid;
}

function sortNodes(unvisitedNodes) {
    unvisitedNodes.sort((a, b) => a.distance - b.distance);
}

function getNeighbors(node, vistedNodes, grid) {
    
    // prettier-ignore
    for (const i of [[0, -1],[0, 1],[-1, 0],[1, 0]]) {
        const temp = [node.row + i[0], node.col + i[1]];
        // check if the neighbor node is within the grid bounds
        if(temp[0] < 0 || temp[0] > grid.length-1 || temp[1] < 0 || temp[1] > grid.length-1){continue};
        
        const neighbor = grid[temp[1]][temp[0]];
        
        if(!neighbor.isWall && !neighbor.isVisited) {
            neighbor.distance = node.distance + 1;
            neighbor.prevNode = node;
            neighbor.isVisited = true;
            vistedNodes.push(neighbor); 
        }
    }
}
