export function recursiveDivision(grid,row, col, height, width, orientation){
    if(width < 3 || height < 3)return;

    let isHorizontal = orientation;

    let newHeight1;
    let newHeight2;
    let newWidth1;
    let newWidth2;
    let col2;
    let row2;
    let wallcol = col;
    let wallrow = row;
    let passageCol;
    let passageRow;
    let hole;
    
    //row = y, col = x
    if(isHorizontal){
        //random placemnet of a wall on even nodes only
        wallrow = row + Math.floor(getRandNum(height - 2)/2) * 2;
        //random placement of a hole in the wall on odds nodes only 
        let rand = getRandNum(width - 2);
        hole = col + Math.floor(rand/ 2) * 2 + 1;
        console.log('whorse-hole', hole)

        newHeight1 = wallrow - row;
        newWidth1 = width;

        newHeight2 = row + height - wallrow;
        newWidth2 = width;

        row2 = wallrow;
        col2 = col;
        
        for(let i = col; i < col+width; i++){
            grid[i][wallrow].isWall = true;
        }
        grid[hole][wallrow].isWall = false;


    }else{
        //random placemnet of a wall on even nodes only
        wallcol = col + Math.floor(getRandNum(width - 2)/ 2) * 2;
        //random placement of a hole in the wall on odds nodes only 
        let rand = getRandNum(height - 2);
        hole = row + Math.floor(rand/ 2)  * 2 + 1;
        console.log('vert-hole', hole)

        newHeight1 = height;
        newWidth1 = wallcol - col;

        newHeight2 = height;
        newWidth2 = row + width - wallcol;

        row2 = row;
        col2 = wallcol;
       
        for(let i = row; i < row + height; i++){
            grid[wallcol][i].isWall = true; 
        }

        grid[wallcol][hole].isWall = false;


    }

    

    console.log("wall (r,c): ", wallrow, wallcol);
    console.log("---------------------");



    //recursiveDivision(grid, row, col, newHeight1, newWidth1, getOrientation(newHeight1, newWidth1));
    recursiveDivision(grid, row2, col2, newHeight2, newWidth2, getOrientation(newHeight2, newWidth2));
    
}

export function getOrientation(height, width){
    if(width < height){//is horizontal
        return true;
    }else if(height < width){ // is vertical
        return false;
    }else{
        const rand = Math.floor(Math.random() * 2);
        return rand === 0  ? true : false;
    }
}

